css:
.section-about-split-v1 {
  --color-bg: #f5f7fa;


  --color-text: #555555;


  padding: 80px 0;
  background-color: var(--color-bg);
  font-family: var(--font-body);
}

.section-about-split-v1 .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 15px;
}

.section-about-split-v1 .row-layout {
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  margin: 0 -15px;
}

.section-about-split-v1 .col-item {
  padding: 0 15px;
  box-sizing: border-box;
}

.section-about-split-v1 .col-text {
  width: 40%;
}

.section-about-split-v1 .col-image {
  width: 60%;
}

.section-about-split-v1 .section-title {
  font-family: var(--font-heading);
  font-size: 2.5rem;
  color: var(--color-dark);
  margin-bottom: 30px;
  font-weight: 400;
}

.section-about-split-v1 .feature-list {
  list-style: none;
  padding: 0;
  margin: 0 0 40px 0;
}

.section-about-split-v1 .feature-list li {
  font-size: 1rem;
  color: var(--color-text);
  margin-bottom: 12px;
  display: flex;
  align-items: center;
}

.section-about-split-v1 .check {
  color: var(--color-primary);
  margin-right: 10px;
  font-weight: bold;
}

.section-about-split-v1 .btn-outline {
  display: inline-block;
  padding: 12px 30px;
  border: 1px solid var(--color-primary);
  border-radius: 30px;
  color: var(--color-primary);
  text-decoration: none;
  text-transform: uppercase;
  font-size: 0.85rem;
  letter-spacing: 1px;
  transition: all 0.3s ease;
}

.section-about-split-v1 .btn-outline:hover {
  background-color: var(--color-primary);
  color: #fff;
}

.section-about-split-v1 .image-wrapper {
  border-radius: 20px;
  overflow: hidden;
  box-shadow: 0 15px 40px rgba(0,0,0,0.1);
}

.section-about-split-v1 .feature-img {
  width: 100%;
  height: auto;
  display: block;
}

@media (max-width: 992px) {
  .section-about-split-v1 .col-text, 
  .section-about-split-v1 .col-image {
    width: 100%;
  }
  .section-about-split-v1 .col-text {
    margin-bottom: 40px;
  }
}
