css:
.section-services-grid-v1 {
  --color-bg: #ffffff;




  padding: 80px 0;
  background-color: var(--color-bg);
  font-family: var(--font-body);
}

.section-services-grid-v1 .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 15px;
}

.section-services-grid-v1 .row-layout {
  display: flex;
  flex-wrap: wrap;
  margin: 0 -15px;
}

.section-services-grid-v1 .header-row {
  justify-content: center;
  text-align: center;
  margin-bottom: 50px;
}

.section-services-grid-v1 .section-heading {
  font-family: var(--font-heading);
  font-size: 2.5rem;
  color: var(--color-dark);
  font-weight: 400;
}

.section-services-grid-v1 .col-item {
  padding: 15px;
  box-sizing: border-box;
}

.section-services-grid-v1 .col-4 {
  width: 33.333%;
}

.section-services-grid-v1 .col-12 {
  width: 100%;
}

.section-services-grid-v1 .grid-card {
  position: relative;
  border-radius: 20px;
  overflow: hidden;
  height: 300px;
  background: var(--color-light);
}

.section-services-grid-v1 .grid-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.5s ease;
}

.section-services-grid-v1 .grid-card:hover .grid-img {
  transform: scale(1.1);
}

.section-services-grid-v1 .card-overlay {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  background: linear-gradient(to top, rgba(0,0,0,0.8), transparent);
  padding: 30px 20px;
  box-sizing: border-box;
  color: #fff;
}

.section-services-grid-v1 .card-overlay h3 {
  font-family: var(--font-heading);
  font-size: 1.2rem;
  margin: 0 0 5px 0;
}

.section-services-grid-v1 .card-overlay p {
  font-size: 0.85rem;
  opacity: 0.8;
  margin: 0;
}

@media (max-width: 992px) {
  .section-services-grid-v1 .col-4 {
    width: 50%;
  }
}

@media (max-width: 576px) {
  .section-services-grid-v1 .col-4 {
    width: 100%;
  }
}
