css:
.section-footer-smart-v1 {
  --color-dark-bg: #1f252b;
  --color-darker: #15191d;
  --color-text: #8fa1b3;
  --color-white: #ffffff;



  font-family: var(--font-body);
}

.section-footer-smart-v1 .cta-bar {
  background-color: var(--color-darker);
  padding: 50px 0;
  border-bottom: 1px solid rgba(255,255,255,0.05);
}

.section-footer-smart-v1 .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 15px;
}

.section-footer-smart-v1 .row-layout {
  display: flex;
  flex-wrap: wrap;
  margin: 0 -15px;
}

.section-footer-smart-v1 .col-item {
  padding: 0 15px;
  box-sizing: border-box;
}

.section-footer-smart-v1 .col-8 {
  width: 66.666%;
}

.section-footer-smart-v1 .col-4 {
  width: 33.333%;
}

.section-footer-smart-v1 .cta-row {
  align-items: center;
}

.section-footer-smart-v1 .cta-bar h2 {
  color: var(--color-white);
  margin: 0 0 10px 0;
  font-family: var(--font-heading);
}

.section-footer-smart-v1 .cta-bar p {
  color: var(--color-text);
  margin: 0;
}

.section-footer-smart-v1 .cta-btn-col {
  text-align: right;
}

.section-footer-smart-v1 .btn-cta {
  background-color: var(--color-primary);
  color: var(--color-white);
  padding: 12px 25px;
  border-radius: 25px;
  text-decoration: none;
  font-weight: 600;
  text-transform: uppercase;
  font-size: 0.85rem;
}

.section-footer-smart-v1 .main-footer {
  background-color: var(--color-dark-bg);
  padding: 70px 0;
  color: var(--color-text);
}

.section-footer-smart-v1 .logo {
  display: flex;
  align-items: center;
  margin-bottom: 20px;
}

.section-footer-smart-v1 .logo-img {
  width: 30px;
  height: 30px;
  margin-right: 10px;
}

.section-footer-smart-v1 .logo span {
  color: var(--color-white);
  font-weight: 700;
  font-size: 1.2rem;
  font-family: var(--font-heading);
}

.section-footer-smart-v1 .footer-desc {
  font-size: 0.9rem;
  line-height: 1.6;
  margin-bottom: 20px;
}

.section-footer-smart-v1 .contact-info p {
  font-size: 0.85rem;
  margin-bottom: 5px;
}

.section-footer-smart-v1 .contact-info a {
  color: var(--color-primary);
  font-size: 0.85rem;
  text-decoration: underline;
}

.section-footer-smart-v1 .footer-heading {
  color: var(--color-white);
  margin: 0 0 25px 0;
  font-size: 1.1rem;
  font-family: var(--font-heading);
}

.section-footer-smart-v1 .phone-number {
  display: block;
  color: var(--color-primary);
  font-size: 1.2rem;
  margin-bottom: 5px;
}

.section-footer-smart-v1 .hours {
  font-size: 0.85rem;
}

.section-footer-smart-v1 .footer-links {
  list-style: none;
  padding: 0;
}

.section-footer-smart-v1 .footer-links li {
  margin-bottom: 10px;
}

.section-footer-smart-v1 .footer-links a {
  color: var(--color-text);
  text-decoration: none;
  transition: color 0.3s;
}

.section-footer-smart-v1 .footer-links a:hover {
  color: var(--color-white);
}

@media (max-width: 768px) {
  .section-footer-smart-v1 .col-8, 
  .section-footer-smart-v1 .col-4 {
    width: 100%;
    text-align: center;
    margin-bottom: 30px;
  }
  .section-footer-smart-v1 .cta-btn-col {
    text-align: center;
  }
}
