css:
.section-hero-smart-v1 {
  --color-bg: #f5f7fa;

  --color-text-muted: #666666;



  position: relative;
  padding-top: 150px;
  padding-bottom: 100px;
  background-color: var(--color-bg);
  font-family: var(--font-body);
}

.section-hero-smart-v1 .hero-bg {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 450px;
  overflow: hidden;
  z-index: 0;
}

.section-hero-smart-v1 .bg-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.section-hero-smart-v1 .overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.6);
}

.section-hero-smart-v1 .container {
  position: relative;
  z-index: 1;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 15px;
}

.section-hero-smart-v1 .row-layout {
  display: flex;
  flex-wrap: wrap;
  margin: 0 -15px;
}

.section-hero-smart-v1 .hero-content {
  margin-bottom: 80px;
  text-align: center;
  justify-content: center;
}

.section-hero-smart-v1 .hero-title {
  font-family: var(--font-heading);
  font-size: 3.5rem;
  font-weight: 700;
  color: #ffffff;
  margin-top: 50px;
}

.section-hero-smart-v1 .cards-row {
  justify-content: center;
}

.section-hero-smart-v1 .col-item {
  padding: 0 15px;
  box-sizing: border-box;
}

.section-hero-smart-v1 .col-3 {
  width: 25%;
}

.section-hero-smart-v1 .col-12 {
  width: 100%;
}

.section-hero-smart-v1 .feature-card {
  background: #ffffff;
  padding: 30px 20px;
  border-radius: 20px;
  text-align: center;
  box-shadow: 0 10px 30px rgba(0,0,0,0.05);
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  transition: transform 0.3s ease;
}

.section-hero-smart-v1 .feature-card:hover {
  transform: translateY(-5px);
}

.section-hero-smart-v1 .icon-wrapper {
  width: 60px;
  height: 60px;
  margin-bottom: 20px;
}

.section-hero-smart-v1 .card-icon {
  width: 100%;
  height: 100%;
  object-fit: contain;
  opacity: 0.7;
}

.section-hero-smart-v1 h3 {
  font-family: var(--font-heading);
  font-size: 1.25rem;
  color: var(--color-dark);
  margin-bottom: 15px;
}

.section-hero-smart-v1 p {
  font-size: 0.9rem;
  color: var(--color-text-muted);
  line-height: 1.6;
  margin-bottom: 25px;
  flex-grow: 1;
}

.section-hero-smart-v1 .card-link {
  font-size: 0.8rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  color: var(--color-dark);
  text-decoration: none;
  border-bottom: 1px solid #ccc;
  padding-bottom: 2px;
  font-weight: 600;
}

@media (max-width: 992px) {
  .section-hero-smart-v1 .col-3 {
    width: 50%;
    margin-bottom: 30px;
  }
}

@media (max-width: 576px) {
  .section-hero-smart-v1 .col-3 {
    width: 100%;
  }
  .section-hero-smart-v1 .hero-title {
    font-size: 2.5rem;
  }
}
