css:
.section-creative-grid-v1 {
  --color-accent-brown: #9b8558;
  --color-bg-dark: #3a3a3a;
  --color-bg-light: #dcdcdc;
  --color-text-light: #ffffff;
  --color-text-dark: #333333;
  --color-text-muted: #f0f0f0;



  padding: 60px 0;
  background-color: #f4f4f4;
}

.section-creative-grid-v1 .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 15px;
}

/* Grid Layout */
.section-creative-grid-v1 .grid-wrapper {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-template-rows: auto auto auto;
  gap: 20px;
}

.section-creative-grid-v1 .col-item {
  position: relative;
  overflow: hidden;
  min-height: 300px;
}

.section-creative-grid-v1 img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
}

.section-creative-grid-v1 .content-wrap {
  padding: 40px;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

/* Specific Item Styling */

/* 1. Brown Box (Top Left) - Spans 2 rows vertically */
.section-creative-grid-v1 .item-brown {
  grid-column: 1 / 2;
  grid-row: 1 / 3;
  background-color: var(--color-accent-brown);
  color: var(--color-text-light);
}

.section-creative-grid-v1 .item-brown .title {
  font-family: var(--font-heading);
  font-size: 24px;
  margin-bottom: 20px;
  font-weight: 400;
  opacity: 0.9;
}

.section-creative-grid-v1 .item-brown .description {
  font-family: var(--font-body);
  font-size: 14px;
  line-height: 1.6;
  margin-bottom: 20px;
  opacity: 0.8;
}

/* 2 & 3. Top Images */
.section-creative-grid-v1 .item-img-1 {
  grid-column: 2 / 3;
  grid-row: 1 / 2;
}

.section-creative-grid-v1 .item-img-2 {
  grid-column: 3 / 4;
  grid-row: 1 / 2;
}

/* 4. Dark Center Box */
.section-creative-grid-v1 .item-dark {
  grid-column: 2 / 3;
  grid-row: 2 / 3;
  background-color: var(--color-bg-dark);
  color: var(--color-accent-brown);
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
}

.section-creative-grid-v1 .item-dark .headline {
  font-family: var(--font-heading);
  font-size: 32px;
  line-height: 1.2;
  text-transform: uppercase;
  font-weight: 700;
  margin: 0;
}

/* 5. Folder Image */
.section-creative-grid-v1 .item-img-3 {
  grid-column: 3 / 4;
  grid-row: 2 / 3;
}

/* 6. Crane Image (Bottom Left) */
.section-creative-grid-v1 .item-img-4 {
  grid-column: 1 / 2;
  grid-row: 3 / 4;
}

/* 7. Light Box (Bottom Right) - Spans 2 cols */
.section-creative-grid-v1 .item-light {
  grid-column: 2 / 4;
  grid-row: 3 / 4;
  background-color: var(--color-bg-light);
  color: var(--color-text-dark);
}

.section-creative-grid-v1 .item-light .text-body {
  font-family: var(--font-body);
  font-size: 15px;
  line-height: 1.8;
  margin-bottom: 30px;
  max-width: 90%;
}

.section-creative-grid-v1 .btn-outline {
  display: inline-block;
  padding: 12px 24px;
  border: 1px solid var(--color-text-dark);
  color: var(--color-text-dark);
  text-decoration: none;
  text-transform: uppercase;
  font-size: 12px;
  letter-spacing: 1px;
  transition: all 0.3s ease;
  align-self: flex-start;
}

.section-creative-grid-v1 .btn-outline:hover {
  background-color: var(--color-text-dark);
  color: var(--color-text-light);
}

/* Responsive */
@media (max-width: 992px) {
  .section-creative-grid-v1 .grid-wrapper {
    grid-template-columns: 1fr 1fr;
  }
  .section-creative-grid-v1 .item-brown { grid-column: 1 / 2; grid-row: 1 / 2; }
  .section-creative-grid-v1 .item-img-1 { grid-column: 2 / 3; grid-row: 1 / 2; }
  .section-creative-grid-v1 .item-img-2 { grid-column: 1 / 2; grid-row: 2 / 3; }
  .section-creative-grid-v1 .item-dark { grid-column: 2 / 3; grid-row: 2 / 3; }
  .section-creative-grid-v1 .item-img-3 { grid-column: 1 / 2; grid-row: 3 / 4; }
  .section-creative-grid-v1 .item-img-4 { grid-column: 2 / 3; grid-row: 3 / 4; }
  .section-creative-grid-v1 .item-light { grid-column: 1 / 3; grid-row: 4 / 5; }
}

@media (max-width: 768px) {
  .section-creative-grid-v1 .grid-wrapper {
    display: flex;
    flex-direction: column;
  }
  .section-creative-grid-v1 .col-item {
    min-height: 250px;
  }
}
