css:
.section-testimonials-cards-v1 {
  --color-bg: #f9f9f9;
  --color-card-bg: #ffffff;

  --color-text: #666666;



  padding: 80px 0;
  background-color: var(--color-bg);
  font-family: var(--font-body);
}

.section-testimonials-cards-v1 .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 15px;
}

.section-testimonials-cards-v1 .row-layout {
  display: flex;
  flex-wrap: wrap;
  margin: 0 -15px;
}

.section-testimonials-cards-v1 .header-row {
  justify-content: center;
  margin-bottom: 50px;
}

.section-testimonials-cards-v1 .section-title {
  font-family: var(--font-heading);
  font-size: 2.2rem;
  color: var(--color-dark);
  text-align: center;
}

.section-testimonials-cards-v1 .col-item {
  padding: 0 15px;
  box-sizing: border-box;
}

.section-testimonials-cards-v1 .col-4 {
  width: 33.333%;
}

.section-testimonials-cards-v1 .col-12 {
  width: 100%;
}

.section-testimonials-cards-v1 .review-card {
  background: var(--color-card-bg);
  padding: 30px;
  border-radius: 15px;
  box-shadow: 0 5px 20px rgba(0,0,0,0.03);
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

.section-testimonials-cards-v1 .author {
  font-family: var(--font-heading);
  font-size: 1.1rem;
  color: var(--color-dark);
  margin: 0 0 15px 0;
  font-weight: 700;
}

.section-testimonials-cards-v1 .review-text {
  font-size: 0.95rem;
  color: var(--color-text);
  line-height: 1.6;
  margin-bottom: 20px;
  flex-grow: 1;
}

.section-testimonials-cards-v1 .date {
  font-size: 0.8rem;
  color: #999;
  text-transform: uppercase;
  letter-spacing: 1px;
  border-top: 1px solid var(--color-border);
  padding-top: 15px;
}

@media (max-width: 992px) {
  .section-testimonials-cards-v1 .col-4 {
    width: 100%;
    margin-bottom: 20px;
  }
}
